    val listData: ArrayList<Snack>
    get() {
        val list = arrayListOf<Snack>()
        for (position in snackNames.indices){
            val snack = Snack()
            snack.name = snackNames[position]
            snack.distArea = snackDistArea[position]
            snack.link = snackLink[position]
            snack.detail = snackDetail[position]
            snack.photo = snackImages[position]
            list.add(snack)
        }
        return list
